        },
        body: JSON.stringify({ email, password }),
      });

      if (response.ok) {
        const userData = await response.json();
        setUser(userData);
        localStorage.setItem('freshcart_user', JSON.stringify(userData));
        return { success: true };
      } else {
        return { success: false, error: 'Invalid credentials' };
      }
    } catch (error) {
      // For demo purposes, simulate successful login
      const demoUser = {
        id: 1,
        name: 'Demo User',
        email: email,
        avatar: 'https://via.placeholder.com/150'
      };
      setUser(demoUser);
      localStorage.setItem('freshcart_user', JSON.stringify(demoUser));
      return { success: true };
    }
  };

  const register = async (name, email, password) => {
    try {
      // Simulate API call
      const response = await fetch('/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, email, password }),
      });

      if (response.ok) {
        const userData = await response.json();
        setUser(userData);
        localStorage.setItem('freshcart_user', JSON.stringify(userData));
        return { success: true };
      } else {
        return { success: false, error: 'Email already exists' };
      }
    } catch (error) {
      // For demo purposes, simulate successful register
      const demoUser = {
        id: 1,
        name: name,
        email: email,
        avatar: 'https://via.placeholder.com/150'
      };
      setUser(demoUser);
      localStorage.setItem('freshcart_user', JSON.stringify(demoUser));
      return { success: true };
    }
  };*/
  

  const logout = () => {
    setUser(null);
    localStorage.removeItem('freshcart_user');
  };

  const value = {
    user,
    login,
    register,
    logout,
    loading
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
